<?php


class Product {


    public $title;
    public $price;
    public $description;
    public $img; 
    protected $category;
    protected $type;

    function __construct(string $title, float $price, string $description, string $img, string|null $category = null, string|null $type = null) {
        $this->title = $title;
        $this->price = $price;
        $this->description = $description;
        $this->img = $img;
        $this->setCategory($category);
        $this->setType($type);

    }

    public function getCategory() {
        return $this->category;
    }

    public function setCategory($category) {

        $categories = [
            'Cani',
            'Gatti',
        ];

        if (is_string($category) 
            &&
            in_array($category, $categories)
            ) {
                $this->category = $category;
        }
        else {
            $this->category = null;

        }
    }

    public function getType() {
        return $this->type;
    }

    public function setType($type) {

        $types = [
            'Food',
            'Toy',
            'Petbed'
        ];

        if (is_string($type) 
            &&
            in_array($type, $types)
            ) {
                $this->type = $type;
        }
        else {
            $this->type = null;

        }
    }

}

$prodottogenericoPerCani = new Product(
    'Prodotto generico', 
    15.95, 
