<?php


class Category {

    public $name;
    public $icon;

    function __construct(string $name, string $icon) {
        $this->name = $name;
        $this->icon = $icon;

    }
}


class Product {


    public $title;
    public $price;
    public $description;
    public $img; 
    protected $category;
    // protected $type;

    function __construct(string $title, float $price, string $description, string $img, Category|null $category = null) {
        $this->title = $title;
        $this->price = $price;
        $this->description = $description;
        $this->img = $img;
        $this->setCategory($category);
        // $this->setType($type);

    }

    public function getCategory() {
        return $this->category;
    }

    public function setCategory(Category|null $category) {
        $this->category = $category;

    }

