trait HasBrand {

    protected $brand;

    public function getBrand() {
        return $this->brand;
    }

    public function setBrand(string $brand) {
        if (strlen($brand) >= 2) {
            $this->brand = $brand;
        }
        else {
            throw new Exception('Nome del brand troppo corto, usare pi√π caratteri!!!');
        }
    }
}

class Food extends Product {

    use HasBrand;

    public $ingredients;

    function __construct(string $title, float $price, string $description, string $img, Category|null $category = null, string $ingredients = null, string $brand = null ) {
        parent::__construct($title, $price, $description, $img, $category);

        $this->ingredients = $ingredients;
        $this->setBrand($brand);

    }
}

class Toy extends Product {

    use HasBrand;

    public $materials;

    function __construct(string $title, float $price, string $description, string $img, Category|null $category = null, string $materials = null, string $brand = null ) {
        parent::__construct($title, $price, $description, $img, $category);
        
        $this->materials = $materials;
        $this->setBrand($brand);

    }
}

class Petbed extends Product {

    use HasBrand;

    public $size;

    function __construct(string $title, float $price, string $description, string $img, Category|null $category = null, string $size = null, string $brand = null ) {
        parent::__construct($title, $price, $description, $img, $category);
        
        $this->size = $size;
        $this->setBrand($brand);

    }
}

